package testing.com.usk.assn2;

public class Q2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = Integer.parseInt(args[0]);
		
		System.out.println(args[0]);
		
		if (i % 2 == 0) {
			System.out.println("Even");
		}
		else {
			System.out.println("Odd");
		}
	}

}
